mapper.update:
update expert_table
set expert_pass = #{expert_pass}, expert_phone2 = #{expert_phone2}, expert_phone3 = #{expert_phone3},
		expert_email1 =  #{expert_email1}, expert_email2 =  #{expert_email2}
where expert_id = #{expert_id}

mapper.select:
select sum(rv_rating1) "rv_rating1",sum(rv_rating2) "rv_rating2",sum(rv_rating3) "rv_rating3",sum(rv_rating4) "rv_rating4",sum(rv_rating5) "rv_rating5",sum(rv_rating6) "rv_rating6"
from review
where rv_expert_id = #{expert_id}
