mapper.select:
select * 
from ( select rownum rnum, num, id, content, reg_date
		from(
			select num, id, content,reg_date
			from comments1 where QNA_INDEX1 = #{num}
			order by reg_date desc
		)
)
where rnum between #{start} and #{end}

mapper.update:
update comments1
set content = #{content}
where num = #{num}
