mapper.select:
select count(*)
from reservation
where rs_exid = #{expert_id} and rs_state = #{state}

select *
from (select rownum rnum, re.*, u.*
			from (select rs.*, rq.*
						from reservation rs, request rq
						where rs.rs_exid = rq.expert_id and rs.rs_uid = rq.writer and rs.rs_state = #{state}
						) re
						inner join user_table u on u.user_id = re.rs_uid
			where re.expert_id = #{expert_id}
			order by re.request_date asc
			) 
where rnum > = #{start} and rnum < = #{end}

select count(*)
from reservation
where rs_exid = #{expert_id} and rs_state > = #{state1} and rs_state < = #{state2}

select *
from (select rownum rnum, re.*, u.*
			from (select rs.*, rq.*
						from reservation rs, request rq
						where rs.rs_exid = rq.expert_id and rs.rs_uid = rq.writer and rs.rs_state > = #{state1} and rs.rs_state < = #{state2}
						) re
						inner join user_table u on u.user_id = re.rs_uid
			where re.expert_id = #{expert_id}
			order by re.request_date asc
			) 
where rnum > = #{start} and rnum < = #{end}

mapper.update:
update reservation
set RS_STATE = 4
where RS_NO = #{rs_no}

mapper.select:
select rq.*, rs.*
from (select r.*, rf.*
		  from request r inner join request_file rf on r.request_no = rf.request_no
		 ) rq inner join reservation rs on rq.request_no = rs.rs_no
where rq.request_no = #{request_no}

select rs.*, u.*
from reservation rs inner join user_table u on rs.rs_uid = u.user_id
where rs.rs_no = #{rs_no}

